import Scorer from './Scorer';

const TopScorers = props => {
  const topScorers = props.topScorers
    // .sort((a, b) => (a.goals < b.goals ? 1 : -1))
    .sort((a, b) => b - a)
    .map(scorer => <Scorer key={scorer.playerId} scorer={scorer} />);

  return (
    <>
      <h2>Top Scorers</h2>
      {topScorers}
    </>
  );
};

export default TopScorers;
