import React from 'react';
import TopScorers from '../components/TopScorers';
import Results from '../components/Results';

class Home extends React.Component {
  state = {
    topScorers: [
      {
        playerId: 1,
        name: 'Joe Bloggs',
        goals: 0
      },
      {
        playerId: 2,
        name: 'Lionel Messi',
        goals: 5
      },
      {
        playerId: 3,
        name: 'Neil Dewing',
        goals: 19
      }
    ],
    results: [
      {
        matchId: 1,
        matchDate: new Date(2019, 9, 8),
        homeMatch: true,
        opponent: 'Leamington Juniors',
        ourScore: 3,
        opponentScore: 1,
        scorers: [1, 2, 2]
      },
      {
        matchId: 2,
        matchDate: new Date(2019, 9, 15),
        homeMatch: false,
        opponent: 'Long Itch Tigers',
        ourScore: 2,
        opponentScore: 1,
        scorers: [1, 1]
      }
    ]
  };

  updateScorerGoals() {
    // this.setState({ number: this.state.number + 1 });
    console.log('UPDATE GOALS STATE');
  }

  render() {
    return (
      <>
        <h1>FootyScores</h1>
        <TopScorers topScorers={this.state.topScorers} onChange={this.updateScorerGoals} />
        <Results results={this.state.results} />
        <h2>Next Match</h2>
      </>
    );
  }
}

export default Home;
