import Scorer from './Scorer';

const TopScorers = props => {
  const handleClick = e => {
    console.log('goals changed');
  };

  const topScorers = props.topScorers.sort((a, b) => b.goals - a.goals).map(scorer => <Scorer key={scorer.playerId} scorer={scorer} onClick={handleClick} />);

  return (
    <>
      <h2>Top Scorers</h2>
      {topScorers}
    </>
  );
};

export default TopScorers;
