const Scorer = props => {
  return (
    <>
      <p>Scorer Name: {props.scorer.name}</p>
      <p>Goals Scored: {props.scorer.goals}</p>

      <input type="number" defaultValue={props.scorer.goals} onChange={event => props.onChange(event, props.scorer.playerId)} />

      <hr />
    </>
  );
};

export default Scorer;
