import Scorer from './Scorer';

const TopScorers = props => {
  const changeGoals = e => {
    console.log(e.target.value);
    props.updateScorerGoals();
  };

  const topScorers = props.topScorers.sort((a, b) => b.goals - a.goals).map(scorer => <Scorer key={scorer.playerId} scorer={scorer} onChange={changeGoals} />);

  return (
    <>
      <h2>Top Scorers</h2>
      {topScorers}
    </>
  );
};

export default TopScorers;
