import Scorer from './Scorer';

const TopScorers = props => {
  const topScorers = props.topScorers.sort((a, b) => b.goals - a.goals).map(scorer => <Scorer key={scorer.playerId} scorer={scorer} updateScorerGoals={event => props.onChange(event, scorer.playerId)} />);

  return (
    <>
      <h2>Top Scorers</h2>
      {topScorers}
    </>
  );
};

export default TopScorers;
